INSERT INTO `news` VALUES ('1', '1', 'About nopCommerce', 'It\'s stable and highly usable. From downloads to documentation, www.nopCommerce.com offers a comprehensive base of information, resources, and support to the nopCommerce community.', '<p>For full feature list go to <a href=\"https://www.nopCommerce.com\">nopCommerce.com</a></p><p>Providing outstanding custom search engine optimization, web development services and e-commerce development solutions to our clients at a fair price in a professional manner.</p>', '', null, null, '', '\0', null, null, null, '2019-08-20 12:44:07.097717');
INSERT INTO `news` VALUES ('2', '1', 'nopCommerce new release!', 'nopCommerce includes everything you need to begin your e-commerce online store. We have thought of everything and it\'s all included! nopCommerce is a fully customizable shopping cart', '<p>nopCommerce includes everything you need to begin your e-commerce online store. We have thought of everything and it\'s all included!</p>', '', null, null, '', '\0', null, null, null, '2019-08-20 12:44:08.097791');
INSERT INTO `news` VALUES ('3', '1', 'New online store is open!', 'The new nopCommerce store is open now! We are very excited to offer our new range of products. We will be constantly adding to our range so please register on our site.', '<p>Our online store is officially up and running. Stock up for the holiday season! We have a great selection of items. We will be constantly adding to our range so please register on our site, this will enable you to keep up to date with any new products.</p><p>All shipping is worldwide and will leave the same day an order is placed! Happy Shopping and spread the word!!</p>', '', null, null, '', '\0', null, null, null, '2019-08-20 12:44:09.097791');
